const BlogPage = () => {
  const [loading, setLoading] = useState(false);
  const [posts, setPosts] = useState([]);
  const [favorites, setFavorites] = useState(new Set());
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("all");
  const [currentPage, setCurrentPage] = useState(1);
  const postsPerPage = 6;

  useEffect(() => {
    setLoading(true);
    // Simulate API call delay
    setTimeout(() => {
      setPosts(mockPosts);
      setLoading(false);
    }, 800);
  }, []);

  const debouncedSearch = debounce((q) => setSearchQuery(q), 300);

  const filteredPosts = posts.filter((p) => {
    const matchCat = selectedCategory === "all" || p.category === selectedCategory;
    const matchSearch =
      p.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
      p.description.toLowerCase().includes(searchQuery.toLowerCase());
    return matchCat && matchSearch;
  });

  const paginatedPosts = filteredPosts.slice(
    (currentPage - 1) * postsPerPage,
    currentPage * postsPerPage
  );

  const toggleFavorite = (id) => {
    setFavorites((prev) => {
      const newFav = new Set(prev);
      newFav.has(id) ? newFav.delete(id) : newFav.add(id);
      return newFav;
    });
  };

  const getCategoryColor = (categoryValue) => {
    return categories.find((c) => c.value === categoryValue)?.color;
  };

  return (
    <div className="bg-gray-100 font-sans text-gray-800">
      {/* Hero Section */}
      <section
        className="relative h-[50vh] flex items-center justify-center text-white"
        style={{
          backgroundImage: `url(https://images.unsplash.com/photo-1522202176988-66273c2fd55f?w=1600)`,
          backgroundSize: "cover",
          backgroundPosition: "center",
        }}
      >
        <div className="absolute inset-0 bg-black/60" />
        <div className="relative text-center px-4">
          <h1 className="text-4xl md:text-6xl font-extrabold mb-4 animate-fadeInDown">
            Blog E-Learning
          </h1>
          <p className="max-w-2xl mx-auto text-lg text-gray-200 animate-fadeInUp">
            Cập nhật xu hướng mới nhất về công nghệ và học tập hiện đại
          </p>
        </div>
      </section>
      
      ---

      {/* Featured Posts Section */}
      <section className="max-w-7xl mx-auto py-16 px-4 sm:px-6 lg:px-8">
        <h2 className="text-3xl font-bold text-center mb-10 text-gray-900">
          Bài viết nổi bật ✨
        </h2>
        {loading ? (
          <div className="flex justify-center py-20">
            <Spin size="large" />
          </div>
        ) : (
          <Swiper
  spaceBetween={30}
  slidesPerView={1}
  modules={[Autoplay]}
  autoplay={{ delay: 4000, disableOnInteraction: false }}
  loop
  className="rounded-xl overflow-hidden" // Added overflow-hidden to contain the scale effect
>
  {posts
    .filter((p) => p.featured)
    .map((p) => (
      <SwiperSlide key={p.id}>
        <div className="relative rounded-xl shadow-2xl group cursor-pointer">
          <img
            src={p.image}
            alt={p.title}
            className="w-full h-[500px] md:h-[600px] object-cover transition-transform duration-500 group-hover:scale-105"
          />
          <div className="absolute inset-0 bg-gradient-to-t from-black/80 via-black/40 to-transparent flex items-end p-8 text-white">
            <div>
              <Tag
                color={getCategoryColor(p.category)}
                className="mb-2 uppercase text-xs font-semibold"
              >
                {categories.find((c) => c.value === p.category)?.label}
              </Tag>
              <h3 className="text-3xl md:text-4xl font-extrabold leading-tight drop-shadow-lg">
                {p.title}
              </h3>
              <p className="text-sm md:text-base text-gray-200 mt-2 line-clamp-2">
                {p.description}
              </p>
              <div className="flex items-center gap-4 mt-3 text-sm text-gray-300">
                <span>
                  <CalendarOutlined className="mr-1" />
                  {new Date(p.date).toLocaleDateString("vi-VN")}
                </span>
                <span className="flex items-center gap-1 text-yellow-400">
                  <StarOutlined />
                  {p.rating}
                </span>
              </div>
            </div>
          </div>
        </div>
      </SwiperSlide>
    ))}
</Swiper>
        )}
      </section>

      ---

      {/* Search and Filter Section */}
      <section className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 mb-12">
        <div className="bg-white p-6 rounded-2xl shadow-lg flex flex-col lg:flex-row gap-4 items-center">
          <Search
            placeholder="Tìm bài viết..."
            enterButton={<SearchOutlined />}
            size="large"
            className="flex-1"
            onChange={(e) => debouncedSearch(e.target.value)}
          />
          <Select
            value={selectedCategory}
            onChange={(value) => {
              setSelectedCategory(value);
              setCurrentPage(1); // Reset page on category change
            }}
            size="large"
            className="min-w-[180px] w-full lg:w-auto"
          >
            {categories.map((cat) => (
              <Option key={cat.value} value={cat.value}>
                {cat.label}
              </Option>
            ))}
          </Select>
        </div>
      </section>

      ---

      {/* Posts Grid Section */}
      <section className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 pb-16">
        {loading ? (
          <div className="flex justify-center py-20">
            <Spin size="large" />
          </div>
        ) : filteredPosts.length > 0 ? (
          <>
            <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-8">
              {paginatedPosts.map((p) => (
                <Card
                  key={p.id}
                  className="shadow-lg hover:shadow-2xl transition-all duration-300 rounded-2xl overflow-hidden"
                  cover={
                    <div className="relative">
                      <img
                        src={p.image}
                        alt={p.title}
                        className="h-52 w-full object-cover rounded-t-2xl"
                      />
                      <Button
                        type="ghost"
                        shape="circle"
                        icon={
                          favorites.has(p.id) ? (
                            <HeartFilled className="text-red-500 text-lg" />
                          ) : (
                            <HeartOutlined className="text-white text-lg" />
                          )
                        }
                        className="absolute top-4 right-4 bg-black/40 border-0 hover:bg-black/60 transition"
                        onClick={() => toggleFavorite(p.id)}
                      />
                    </div>
                  }
                >
                  <Tag color={getCategoryColor(p.category)} className="mb-2">
                    {categories.find((c) => c.value === p.category)?.label}
                  </Tag>
                  <h3 className="mt-2 text-xl font-bold hover:text-blue-600 transition-colors cursor-pointer">
                    {p.title}
                  </h3>
                  <p className="text-gray-600 text-sm mt-1 line-clamp-3">
                    {p.description}
                  </p>
                  <div className="flex justify-between items-center mt-4 text-sm text-gray-500">
                    <span className="flex items-center">
                      <CalendarOutlined className="mr-1" />
                      {new Date(p.date).toLocaleDateString("vi-VN")}
                    </span>
                    <span className="flex items-center gap-1 text-yellow-500">
                      <StarOutlined />
                      {p.rating}
                    </span>
                  </div>
                </Card>
              ))}
            </div>
            <div className="flex justify-center mt-12">
              <Pagination
                current={currentPage}
                pageSize={postsPerPage}
                total={filteredPosts.length}
                onChange={setCurrentPage}
                showSizeChanger={false}
              />
            </div>
          </>
        ) : (
          <div className="text-center py-20 rounded-xl bg-white shadow-lg">
            <div className="text-6xl mb-4">😔</div>
            <h3 className="text-xl font-bold">Không tìm thấy bài viết nào</h3>
            <p className="text-gray-600">
              Hãy thử thay đổi từ khóa hoặc bộ lọc của bạn.
            </p>
          </div>
        )}
      </section>
      
      ---

      {/* Newsletter Section */}
      <section className="bg-gradient-to-r from-blue-600 to-purple-600 text-white py-16">
        <div className="max-w-xl mx-auto text-center px-4">
          <h2 className="text-3xl font-bold mb-4 drop-shadow">
            Đừng bỏ lỡ bài viết mới 📧
          </h2>
          <p className="mb-6 text-gray-200">
            Đăng ký nhận thông tin về công nghệ và học tập.
          </p>
          <div className="flex flex-col sm:flex-row gap-3">
            <Input
              placeholder="Email của bạn"
              size="large"
              className="flex-1 rounded-full shadow-inner"
            />
            <Button
              type="primary"
              size="large"
              className="bg-white text-blue-600 rounded-full font-semibold px-8 hover:bg-gray-200 transition"
            >
              Đăng ký
            </Button>
          </div>
        </div>
      </section>
    </div>
  );
};

export default BlogPage;